USE ny_taxi_ldw;

CREATE EXTERNAL DATA SOURCE nyc_taxi_src1
WITH
  ( 
    LOCATION = 'https://marlabsnycanalyticsdl.dfs.core.windows.net/nyc-taxi-data' 
  );

CREATE EXTERNAL FILE FORMAT csv_file_format2
WITH (
        FORMAT_TYPE = DELIMITEDTEXT,
        FORMAT_OPTIONS ( 

             FIELD_TERMINATOR = ',',
            STRING_DELIMITER = '"',
            FIRST_ROW = 2,
            USE_TYPE_DEFAULT = FALSE, 
            ENCODING = 'UTF8',
            PARSER_VERSION = '2.0'
        ) 
    );

-- Create a new external table
CREATE EXTERNAL TABLE bronze.taxi_zone2
    ( 
        location_id SMALLINT,
        borough VARCHAR(15),
        zone VARCHAR(50),
        service_zone VARCHAR(15)
     )
    WITH (
        LOCATION = 'raw/taxi_zone.csv',
        DATA_SOURCE = nyc_taxi_src,
        FILE_FORMAT = csv_file_format2
    );


SELECT * FROM bronze.taxi_zone2;
